require 'spec_helper'

describe Griddler::AmazonS3SES::Adapter do
  before do
    # mock the hash check on the notification, as we've zero'd the numbers
    allow_any_instance_of(AWS::SnsMessage).to receive(:authentic?).and_return(true)
  end

  it 'registers itself with griddler' do
    expect(Griddler.adapter_registry[:amazon_s3_ses]).to eq Griddler::AmazonS3SES::Adapter
  end

  describe "Griddler shared examples" do
    before do
      sns_message[:mail][:commonHeaders][:to] = ['Hello World <hi@example.com>']
      sns_message[:mail][:commonHeaders][:cc] = ['emily@example.com']
      sns_message[:mail][:commonHeaders][:from] = ['There <there@example.com>']

      allow_any_instance_of(Griddler::AmazonS3SES::Adapter).to receive(:sns_json).and_return(default_params)
      allow(Aws::S3::Resource).to receive(:new).and_return(Aws::S3::Resource.new(stub_responses: true))
    end

    it_behaves_like 'Griddler adapter', :amazon_s3_ses, {}
  end

  describe '.normalize_params' do
    before do
      allow(Aws::S3::Resource).to receive(:new).and_return(Aws::S3::Resource.new(stub_responses: true))
    end

    it 'parses out the "to" addresses, returning an array' do
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:to]).to eq ['"Mr Fugushima at Fugu, Inc" <hi@example.com>', 'Foo bar <foo@example.com>']
    end

    it 'parses out the "from" address, returning a string' do
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:from]).to eq "Test There <there@example.com>"
    end

    it 'parses out the "subject", returning a string' do
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:subject]).to eq "Test"
    end

    it 'should return the text body in UTF-8' do
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:text].encoding.to_s).to eq "UTF-8"
    end

    it 'should return the text body content for non-multi-part email' do
      message = Mail.read_from_string(mail_content)
      allow_any_instance_of(Griddler::AmazonS3SES::Adapter).to receive(:message).and_return(message)
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:text]&.strip).to eq "Hi"
    end

    it 'should return the text body content for non-multi-part email' do
      message = Mail.read_from_string(mail_content_html_body)
      allow_any_instance_of(Griddler::AmazonS3SES::Adapter).to receive(:message).and_return(message)
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:text]&.strip).to eq "Hi"
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:html]&.strip).to eq "<!DOCTYPE html><html><head><META http-equiv=3D\"Content-Type\" content=3D\"text/html;charset=3Dus-ascii\"></head><body><!--Begin Body--><p>Hi</p><!--End Body--></body></html>"
    end

    it 'should return the text body content for multi-part email' do
      message = Mail.read_from_string(mail_content_multipart_text_and_html)
      allow_any_instance_of(Griddler::AmazonS3SES::Adapter).to receive(:message).and_return(message)
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:text]&.strip).to eq "Hello! I am a multi-part text."
    end

    it 'should return empty string for multi-part email with no text content' do
      message = Mail.read_from_string(mail_content_multipart_html_only)
      allow_any_instance_of(Griddler::AmazonS3SES::Adapter).to receive(:message).and_return(message)
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:text]&.strip).to eq ""
    end

    it 'should return simplified string for text/html body' do
      message = Mail.read_from_string(mail_content_complex_text)
      allow_any_instance_of(Griddler::AmazonS3SES::Adapter).to receive(:message).and_return(message)
      expected_string = "Hey User,You've received a response! Let's not leave the customer waiting for too long.Reach out to them ASAP!Message&nbsp;[name]&nbsp;ContactMessageToday 3:26 PMYour customer just responded!Campaign: The changeMessage: \"Stop\"Click here to Reply!"
      expect(Griddler::AmazonS3SES::Adapter.normalize_params(default_params)[:text]&.strip).to eq expected_string
    end
  end

